" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Diagnostic approach to abdominal pain in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/36/19018/contributors\">",
