var title_f18_36_19020="Causes of pediatric hyperkalemia";
var content_f18_36_19020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F85965&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F85965&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of hyperkalemia in children based on pathophysiology",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cause",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Increased potassium intake (rare cause of hyperkalemia with the exception of children with chronic kidney disease)",
"       </td>",
"       <td>",
"        Exposure to high potassium loads in intravenous fluids or medications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exposure to potassium containing medications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Massive transfusions of stored blood",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Transcellular potassium movement",
"       </td>",
"       <td>",
"        Structural cellular damage due to:",
"        <ul>",
"         <li>",
"          Hemolysis",
"         </li>",
"         <li>",
"          Rhabdomyolysis",
"         </li>",
"         <li>",
"          Tumor lysis",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No structural cellular injury:",
"        <ul>",
"         <li>",
"          Metabolic acidosis",
"         </li>",
"         <li>",
"          Diabetic ketoacidosis",
"         </li>",
"         <li>",
"          Hyperkalemia periodic paralysis",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Abnormal renal potassium excretion",
"       </td>",
"       <td>",
"        Decreased effective circulating volume",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased RAAS activity:",
"        <ul>",
"         <li>",
"          Congenital adrenal hyperplasia",
"         </li>",
"         <li>",
"          Adrenal insufficiency",
"         </li>",
"         <li>",
"          Drug effect (ACE inhibitor/ARB, eplerenone, spironolactone, or aliskiren)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant renal impairment with either acute or chronic loss of GFR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired tubular potassium secretion:",
"        <ul>",
"         <li>",
"          Reflux nephropathy",
"         </li>",
"         <li>",
"          Obstructive uropathy",
"         </li>",
"         <li>",
"          Sickle cell nephropathy",
"         </li>",
"         <li>",
"          Drug effect (amiloride, triamterene)",
"         </li>",
"         <li>",
"          Hypoaldosteronism (type IV renal tubular acidosis)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Pseudohyperkalemia",
"       </td>",
"       <td>",
"        Hemolyzed specimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leukocytosis or thrombocytosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RAAS: renin-angiotensin-aldosterone system; ACE inhibitor/ARB: angiotensin converting enzyme inhibitor/angiotensin receptor blocker; GFR: glomerular filtration rate.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
