var title_f18_37_19036="Treatment of neuronopathic GD";
var content_f18_37_19036=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50695&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50695&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of European consensus guidelines for treatment of neuronopathic Gaucher disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Enzyme replacement therapy (ERT) with imiglucerase is the treatment of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Indications:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients with",
"        <strong>",
"         chronic",
"        </strong>",
"        neuronopathic Gaucher disease*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Siblings of patients with neuronopathic Gaucher disease who are proven to have Gaucher disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients with the following high-risk genotypes:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        - L444P/L444P (c.1448T&gt;C homozygote)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        - D409H/D409H (c.1342G&gt;C homozygote)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        - L444P/D409H (c.1448T&gt;C/c.1342G&gt;C heterozygote)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Onset of severe systemic Gaucher disease at &le;2 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Dose:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        60 units/kg every two weeks&bull;, initiated as soon as possible after diagnosis or identification.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Duration:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Treatment with 60 units/kg every two weeks should be continued until the patient attains adulthood and clearly has mild Gaucher disease and stable neurologic involvement. At this point a dosage reduction (to as low as 30 units/kg every two weeks) may be considered.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Splenectomy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Total splenectomy should be avoided if at all possible. Partial, rather than total, splenectomy should be considered if splenectomy is required in an emergency situation.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * ERT is",
"     <strong>",
"      not",
"     </strong>",
"     recommended for patients with acute disease.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Higher doses may be necessary to control visceral disease.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Vellodi A, Bembi B, de Villemeur TB, et al. Management of neuronopathic Gaucher disease: a European consensus. J Inherit Metab Dis 2001; 24:319 and Vellodi A, Tylki-Szymanska A, Davies EH, et al. Management of neuronopathic Gaucher disease: Revised recommendations. J Inherit Metab Dis 2009; 32:660.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
