var title_f18_38_19051="Regimens for toxoplasmosis";
var content_f18_38_19051=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F75331&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F75331&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Standard treatment regimens for toxoplasmosis in patients with AIDS",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose/Route",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adverse reactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Pyrimethamine",
"        <strong>",
"         PLUS",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Loading - 200 mg PO followed by 30-75 mg/day for three to six weeks",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Bone marrow suppression, vomiting, diarrhea, seizures, hematuria, dysrhythmias, rash",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sulfadiazine",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other\">",
"        1-1.5 grams every 6 hours PO for three to six weeks",
"       </td>",
"       <td class=\"sublist_other\">",
"        Bone marrow suppression, rash, nausea, nephrotoxicity, encephalopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clindamycin",
"       </td>",
"       <td class=\"sublist_other\">",
"        1200 mg every 6 hours PO for 3 weeks, followed by 300 mg every 6 hours for 3 weeks",
"       </td>",
"       <td class=\"sublist_other\">",
"        Nausea, vomiting, diarrhea, rash, neutropenia, pseudomembranous colitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leucovorin",
"       </td>",
"       <td>",
"        10-20 mg PO/IV/IM per day",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
