var title_f18_43_19131="Criteria for outpatient management of PPROM";
var content_f18_43_19131=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F55156&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F55156&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested criteria for home management of women with PPROM",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Reliable patient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dependable transportation and telephone service",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lives near hospital",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluation in hospital for 72 hours before discharge home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient to check her pulse and temperature every 6 hours and notify physician if temperature &gt;98.6 degrees F (37 degrees C) or pulse &gt;100 beats/minute",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fetal kick counts daily and notify physician if fewer than 10 fetal movements in a two hour period",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonstress test and complete blood count twice a week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Weekly ultrasound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cephalic presentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No evidence of infection or labor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presence of a vertical amniotic fluid pocket &gt;2 cm on ultrasound",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: data in Bartfield MC, Carlan SJ. Clin Obstet Gynecol 1998; 41:503.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
