" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of the treatment of newly diagnosed, non-metastatic breast cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Alphonse Taghian, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Moataz N El-Ghamry, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Sofia D Merajver, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Daniel F Hayes, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/46/19177/contributors\">",
"     Don S Dizon, MD, FACP",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?18/46/19177/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
"   <a href=\"/home/editorial-policy\" target=\"_blank\">",
"    peer review process",
"   </a>",
"   <span>",
"    is complete.",
"   </span>",
"  </div>",
"  <div id=\"literatureReviewDate\">",
"   <span class=\"emphasis\">",
"    Literature review current through:",
"   </span>",
"   Oct 2013.",
"   <span class=\"pipeSpace\">",
"    |",
"   </span>",
"   <span class=\"emphasis\">",
