var title_f18_46_19179="Criteria schizotype personality";
var content_f18_46_19179=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F75507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F75507&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for schizotypal personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Five or more of following plus general criteria for personality disorder:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Ideas of reference",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Odd beliefs or magical thinking, that influence behavior and are inconsistent with subcultural norms",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Unusual perceptual experiences, including bodily illusions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Odd thinking and speech",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Suspiciousness or paranoid ideation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Inappropriate or constricted affect",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Behavior or appearance that is odd, eccentric, or peculiar",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       8. Lack of close friends or confidants other than first-degree relatives",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       9. Excessive social anxiety that does not diminish with familiarity and tends to be associated with paranoid fears rather than negative judgments about self",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
