var title_f18_46_19181="Weight-based dosing of anesthetic medications in severe obesity";
var content_f18_46_19181=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F63370&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F63370&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Weight-based dosing of anesthetic, analgesic, and adjunctive medications in severe obesity",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Weight for calculating dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Notes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Induction agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Propofol",
"       </td>",
"       <td class=\"centered\">",
"        <p>",
"         LBW (induction/bolus dose)",
"        </p>",
"        Scale maintenance dose to TBW",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Etomidate",
"       </td>",
"       <td class=\"centered\">",
"        LBW",
"       </td>",
"       <td>",
"        Data evaluating etomidate dosing in obesity is scant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Thiopental*",
"       </td>",
"       <td class=\"centered\">",
"        LBW",
"       </td>",
"       <td>",
"        Duration of effect may be prolonged in obese patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Opioids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fentanyl",
"       </td>",
"       <td class=\"centered\">",
"        IBW or LBW",
"       </td>",
"       <td>",
"        Dose needs to be reduced for prolonged infusion due to drug accumulation in non-lean tissues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Remifentanil",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sufentanil",
"       </td>",
"       <td class=\"centered\">",
"        TBW or LBW",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hydromorphone",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td rowspan=\"2\">",
"        Duration of effect prolonged in obese patients after repeated dosing or use of continuous infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Morphine sulfate",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Benzodiazepines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lorazepam",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td>",
"        Duration of effect prolonged in obese patients after repeated dosing or continuous infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Midazolam",
"       </td>",
"       <td class=\"centered\">",
"        LBW",
"       </td>",
"       <td>",
"        Duration of effect prolonged in obese patients after repeated dosing or continuous infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Neuromuscular blocking agents (non-depolarizing)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atracurium",
"       </td>",
"       <td class=\"centered\">",
"        TBW or LBW",
"       </td>",
"       <td>",
"        Use of TBW for dosing of obese patients provides similar onset but prolonged duration of action compared with non-obese patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cisatracurium",
"       </td>",
"       <td class=\"centered\">",
"        IBW or LBW",
"       </td>",
"       <td>",
"        Use of IBW for dosing of obese patients can modestly increase time to onset and decrease duration of effect compared with non-obese patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mivacurium*",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td>",
"        Obese patients may experience prolonged neuromuscular blockade by scaling dose to TBW and may experience transient hypotension on induction of anesthesia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rocuronium",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td>",
"        Use of IBW for dosing obese patients provides normal onset of effect. Higher doses will provide a faster onset but may result in significantly prolonged duration of effect.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vecuronium",
"       </td>",
"       <td class=\"centered\">",
"        IBW",
"       </td>",
"       <td>",
"        Use of IBW for dosing obese patients provides normal duration of effect. However, time to onset may be slightly prolonged.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Neuromuscular blocking agents (depolarizing)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Succinylcholine",
"       </td>",
"       <td class=\"centered\">",
"        TBW",
"       </td>",
"       <td>",
"        Best intubating conditions are most likely with this induction dose in morbidly obese patients",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Doses must be&nbsp;adjusted according to patient condition and clinical criteria including peripheral nerve&nbsp;monitoring for neuromuscular blocking agents.",
"    <div class=\"footnotes\">",
"     TBW: total (or actual) body weight; IBW: ideal body weight (also known as standard adult dose); LBW: lean body weight.",
"     <br>",
"      * Not available in US.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
