"  <div id=\"topicTitle\">",
"   Pharmacotherapy for alcohol abuse and dependence",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Bankole A Johnson, DSc, MD, PhD, MPhil, FRCPsych",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Richard Saitz, MD, MPH, FACP, FASAM",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/48/19209/contributors\">",
"     Richard Hermann, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
