var title_f18_48_19213="Evaluation of the patient with postoperative fever";
var content_f18_48_19213=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F79357&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F79357&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of the patient with postoperative fever",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            History",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Review record for:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Preoperative course and presentation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Operation (emergent or elective, intraoperative complications)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Postoperative course",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Past medical history and underlying medical problems",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Allergies",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Medications",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Location of catheters and time of placement",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Ask nursing staff about:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Sputum amount and quality",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Diarrhea",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Any areas of skin breakdown or rashes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Ask patient about:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cough",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Pain",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Physical examination",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Review record for vital signs. Determine range in the past day and peak daily values during hospital stay. Check nurses' notes for fevers not recorded in the vital signs chart.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Temperature",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Heart rate",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Respiratory rate",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Examine:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Skin for rash, ecchymoses, injection site erythema, and hematoma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Lungs",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Heart for tachycardia or new murmur",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Abdomen for tenderness, bloating, bowel sounds",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Operative site and lymphatic drainage",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Catheter entry sites",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Lower legs for evidence of deep vein thrombosis",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"            Laboratory*",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Urinalysis and culture",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Sputum gram stain and culture",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Blood culture (from catheters and peripherally - minimum of two)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Wound culture",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Complete blood count with differential",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Chest radiograph",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\" rowspan=\"1\">",
"            Additional blood or radiographic studies might be indicated by specific findings. As examples, abdominal pain might indicate the need for blood tests for hepatic and pancreatic enzymes or abdominal CT scanning; unilateral leg edema might indicate the need for ultrasound to rule out DVT.",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Studies should be ordered based upon the patient evaluation; no test is mandatory to obtain.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
