var title_f18_51_19260="Radiology for urolithiasis";
var content_f18_51_19260=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F65056&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F65056&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Radiologic procedures in evaluating renal stones",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        KUB",
"       </td>",
"       <td>",
"        <p>",
"         Readily available",
"        </p>",
"        <p>",
"         Inexpensive",
"        </p>",
"        <p>",
"         Limited radiation",
"        </p>",
"        <p>",
"         Useful in acute setting",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Requires skilled radiologist to interpret",
"        </p>",
"        <p>",
"         Limited sensitivity and specificity",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ultrasound",
"       </td>",
"       <td>",
"        <p>",
"         Readily available",
"        </p>",
"        <p>",
"         Roughly equivalent to IVP as a diagnostic test",
"        </p>",
"        <p>",
"         Improved sensitivity with use of color Doppler",
"        </p>",
"        <p>",
"         No radiation exposure",
"        </p>",
"        <p>",
"         Good for hydronephrosis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Moderately expensive",
"        </p>",
"        <p>",
"         Poor performance with small stones",
"        </p>",
"        <p>",
"         Requires skilled technician and radiologist",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Excretory urography (IVP)",
"       </td>",
"       <td>",
"        <p>",
"         Useful in planning therapy and confirming diagnosis",
"        </p>",
"        <p>",
"         Long established history as gold standard",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Moderately expensive",
"        </p>",
"        <p>",
"         Intravenous contrast required",
"        </p>",
"        <p>",
"         Moderate x-ray exposure",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CT scan (including spiral CT)",
"       </td>",
"       <td>",
"        <p>",
"         Probably new gold standard",
"        </p>",
"        <p>",
"         Can distinguish radiolucent stones from blood or tumor",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Expensive",
"        </p>",
"        <p>",
"         Moderate x-ray exposure",
"        </p>",
"        <p>",
"         Not uniformly available",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MRI",
"       </td>",
"       <td>",
"        Great potential for localizing site of stone in ureter",
"       </td>",
"       <td>",
"        <p>",
"         Very expensive",
"        </p>",
"        <p>",
"         Largely investigational so far except in certain centers",
"        </p>",
"        <p>",
"         Poorly tolerated by many patients",
"        </p>",
"        <p>",
"         Stones are not affected by magnet and may be missed",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
