var title_f18_51_19261="Bethesda class cervix cytol";
var content_f18_51_19261=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F71416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F71416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Bethesda classification of cervical cytology",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Specimen type",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Conventional smear, liquid-based, or other technique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Specimen adequacy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Satisfactory for evaluation (description includes quality indicators, including endocervical/transformation zone component and obscuring blood or inflammation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unsatisfactory due to... (specify reason)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General categorization (optional)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Negative for intraepithelial lesion or malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Epithelial cell abnormality (specify squamous or glandular)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other: see interpretation/result (eg, endometrial cells in woman &ge;age 40)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Automated review",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If examined by a device, specify device and result",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ancillary testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Describe method and result (eg, molecular testing)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Interpretation/result",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Negative for intraepithelial lesion or malignancy (when there is absence of neoplasia this should be stated specifically, regardless of other findings)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        In addition describe, if present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Infection (Trichomonas vaginalis, Candida spp, shift in flora consistent with bacterial vaginosis, Actinomyces spp, cellular changes)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Other nonneoplastic findings, such as, but not limited to:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Reactive cellular changes associated with inflammation/cellular repair, radiation, or an intrauterine contraceptive device",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Glandular cells post hysterectomy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Atrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endometrial cells (in a woman &ge;age 40) and specify whether negative for squamous intraepithelial lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Epithelial cell abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_single\">",
"        Squamous cell",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\">",
"        Atypical squamous cells (ASC)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        of undetermined significance (ASC-US)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        cannot exclude HSIL (ASC-H)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Low grade squamous intraepithelial lesion (LSIL) cellular changes consistent with HPV, mild dysplasia, CIN 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\">",
"        High grade squamous intraepithelial lesion (HSIL) moderate/severe dysplasia, CIN 2, CIN 3, CIS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        indicate if there are features suspicious for invasion (if invasion suspected)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Squamous cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_single\">",
"        Glandular cell",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\">",
"        Atypical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        Endocervical cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        Endometrial cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        Not otherwise specified",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\">",
"        Atypical, favor neoplastic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        Endocervical cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3\">",
"        Not otherwise specified",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Endocervical adenocarcinoma in situ (AIS)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Other malignant neoplasms (specify)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Educational notes and suggestions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suggestions should be concise and consistent with clinical follow-up guidelines published by professional organizations",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Solomon D, Davey D, Kurman R, et al. The 2001 Bethesda system: terminology for reporting results of cervical cytology. JAMA 2002; 287:2114.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
