var title_f18_57_19355="Animal dander avoid PI";
var content_f18_57_19355=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F58126&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F58126&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tips for avoiding animal allergens (urine, saliva, or dander) in your home",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        If possible, remove animals from your home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Keep any animals outside, in the garage, or in a kennel. Keeping them out of certain rooms in the house will",
"        <strong>",
"         not",
"        </strong>",
"        remove animal allergens from your home.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        After you remove the animal, clean your home thoroughly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        If you cannot remove the animals from your home, try these steps",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use an air filter with a \"HEPA filter,\" but keep in mind that air filters help only a little. Most of the animal allergens in your home are not floating in the air. Air filters remove only allergens that are floating in the air.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the number of things in your home where allergens can build up. Examples include carpets, fabric-covered furniture, and drapes. Replace these with bare floors, furniture that is not covered with fabric, and window blinds.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every week, vacuum with a vacuum cleaner than has a \"HEPA filter\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If you are allergic to your dog, bathing your dog 2 times a week might help reduce your symptoms. If you are allergic to your cat, bathing your cat will probably not reduce your symptoms.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
