var title_f18_58_19371="Laboratory findings in catatonia";
var content_f18_58_19371=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F57370&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F57370&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Laboratory findings in catatonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Laboratory finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Implication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Leukocytosis",
"       </td>",
"       <td>",
"        Non-specific; levels between 15,000 and 25,000 per mL correlate with malignant catatonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased creatinine kinase (CK)",
"       </td>",
"       <td>",
"        Non-specific; levels exceeding 1000 IU/L (normal range &lt;150 IU/L) strongly correlate with malignant catatonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low serum iron",
"       </td>",
"       <td>",
"        Observed in 40 percent of persons with malignant catatonia",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Northoff G, Wenke J, Pflug B. Increase of serum creatine phosphokinase in catatonia: an investigation in 32 acute catatonic patients. Psychol Med 1996; 26:547.",
"       </li>",
"       <li>",
"        Lee JW. Serum iron in catatonia and neuroleptic malignant syndrome. Biol Psychiatry 1998; 44:499.",
"       </li>",
"       <li>",
"        Carroll BT, Goforth HW. Serum iron in catatonia. Biol Psychiatry 1995; 38:776.",
"       </li>",
"       <li>",
"        Singerman B, Raheja R. Malignant catatonia - a continuing reality. Ann Clin Psychiatry 1994; 6:259.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
