"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H15714726\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY &amp; RECOMMENDATIONS",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H11328209\">",
"      WHAT IS A LOW PLATELET COUNT?",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H3\">",
"      OFFICE PRESENTATIONS",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H4\">",
"      ISOLATED ASYMPTOMATIC THROMBOCYTOPENIA",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H5\">",
"      Laboratory error",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H6\">",
"      - Repeat testing",
"     </a>",
"    </li>",
"    <li class=\"dashItem\">",
"     <a class=\"outlineLink\" href=\"#H7\">",
"      - Pseudothrombocytopenia",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
