var title_f18_60_19396="Infiltrating gastric cancer UGI";
var content_f18_60_19396=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F62794&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F62794&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic2\" style=\"width: 1040px;\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Infiltrating gastric carcinoma",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 284px; height: 341px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Upper GI series showing infiltrating gastric carcinoma in the region of the incisura. There is irregular narrowing affecting both the lesser and greater curvatures (arrow).",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Paul C Schroy III, MD.",
"    </div>",
"   </div>",
"  </div>",
"  <div class=\"figure\" style=\"width: 450px\">",
"   <div class=\"ttl\">",
"    Normal upper GI series",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 286px; height: 330px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Normal air-contrast upper gastrointestinal study showing normal gastric folds and small intestinal anatomy, and no masses.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Paul C Schroy III, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
