var title_f18_60_19403="Criteria paranoid personality";
var content_f18_60_19403=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F52389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F52389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for paranoid personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Four or more of following plus general criteria for personality disorder:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Suspects, without sufficient basis, that others are exploiting or deceiving him or her",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Is preoccupied with unjustified doubts about the loyalty or trustworthiness of friends or associates",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Is reluctant to confide in others because of unwarranted fear that the information will be used maliciously against him or her",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Reads hidden demeaning or threatening meanings into benign remarks or events",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Persistently bears grudges, ie, unforgiving of insults, injuries, or slights",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Perceives attacks on his or her character or reputation that are not apparent to others and is quick to react angrily or counterattack",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Has recurrent suspicions, without justification, regarding fidelity of spouse or sexual partner",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
