" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Short QT syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/61/19417/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/61/19417/contributors\">",
"     Charles Antzelevitch, PhD, FACC, FAHA, FHRS",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?18/61/19417/contributors\">",
"     Jonathan M Cordeiro, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/61/19417/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
