var title_f18_61_19419="Dosing of antiviral medications for genital HSV";
var content_f18_61_19419=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F80971&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F80971&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dosing of antiviral medications for adults with genital herpes simplex virus infection in persons with HIV",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical setting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Acyclovir*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Valacyclovir",
"       </td>",
"       <td class=\"subtitle1\">",
"        Famciclovir",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        First episode or recurrence",
"       </td>",
"       <td>",
"        400 mg&nbsp;orally three times daily&nbsp;for 5 to 10 days&bull;",
"       </td>",
"       <td>",
"        1 gram&nbsp;orally twice daily&nbsp;for 5 to10 days&bull;",
"       </td>",
"       <td>",
"        <p>",
"         500 mg&nbsp;orally twice daily&nbsp;for 5 to10 days&bull;",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suppression",
"       </td>",
"       <td>",
"        400 to 800 mg orally&nbsp;twice daily or three times daily",
"       </td>",
"       <td>",
"        500 mg orally twice daily",
"       </td>",
"       <td>",
"        500 mg orally&nbsp;twice daily",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    HIV: human immunodeficiency virus; HSV: herpes simplex virus",
"    <div class=\"footnotes\">",
"     * Intravenous (IV) acyclovir therapy should be provided for patients who have severe HSV disease or complications that necessitate hospitalization.",
"     <br>",
"      &bull; &nbsp;Length of therapy dependent upon resolution of lesions. Therapy should be continued for longer than 10 days if lesions persist.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Workowski KA, Berman S. Sexually transmitted diseases treatment guidelines, 2010. MMWR Recomm Rep 2010; 59(RR-12):23-4.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
