" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   The difficult airway in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/7/18552/contributors\">",
