" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathophysiology and management of fever in infants and children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?18/8/18570/contributors\">",
"     Author",
"    </a>",
"    <br/>",
