var title_f19_0_19468="Causes of bacterial meningitis";
var content_f19_0_19468=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F73706&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F73706&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristic features of common causes of bacterial meningitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Organism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Site of entry",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age range",
"       </td>",
"       <td class=\"subtitle1\">",
"        Predisposing conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Neisseria meningitidis",
"        </em>",
"       </td>",
"       <td>",
"        Nasopharynx",
"       </td>",
"       <td>",
"        All ages",
"       </td>",
"       <td>",
"        Usually none, rarely complement deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Streptococcus pneumoniae",
"        </em>",
"       </td>",
"       <td>",
"        Nasopharynx, direct extension across skull fracture, or from contiguous or distant foci of infection",
"       </td>",
"       <td>",
"        All ages",
"       </td>",
"       <td>",
"        All conditions that predispose to pneumococcal bacteremia, fracture of cribriform plate, cochlear implants, CSF otorrhea from basilar skull fracture, defects of the ear ossicle (Mondini defect)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Listeria monocytogenes",
"        </em>",
"       </td>",
"       <td>",
"        Gastrointestinal tract, placenta",
"       </td>",
"       <td>",
"        Elderly adults and neonates",
"       </td>",
"       <td>",
"        Defects in cell-mediated immunity (eg, glucocorticoids, transplantation [especially renal transplantation]), pregnancy, liver disease, alcoholism, malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coagulase-negative staphylococci",
"       </td>",
"       <td>",
"        Foreign body",
"       </td>",
"       <td>",
"        All ages",
"       </td>",
"       <td>",
"        Surgery and foreign body, especially ventricular drains",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Staphylococcus aureus",
"        </em>",
"       </td>",
"       <td>",
"        Bacteremia, foreign body, skin",
"       </td>",
"       <td>",
"        All ages",
"       </td>",
"       <td>",
"        Endocarditis, surgery and foreign body, especially ventricular drains; cellulitis, decubitus ulcer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gram-negative bacilli",
"       </td>",
"       <td>",
"        Various",
"       </td>",
"       <td>",
"        Elderly adults and neonates",
"       </td>",
"       <td>",
"        Advanced medical illness, neurosurgery, ventricular drains, disseminated strongyloidiasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         Haemophilus influenzae",
"        </em>",
"       </td>",
"       <td>",
"        Nasopharynx, contiguous spread from local infection",
"       </td>",
"       <td>",
"        Adults now, but infants and children if not vaccinated",
"       </td>",
"       <td>",
"        Diminished humoral immunity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
