var title_f19_1_19484="ART for occupational PEP against HIV";
var content_f19_1_19484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F83600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F83600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antiretroviral regimens for occupational postexposure prophylaxis of HIV infection*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Preferred regimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenofovir-emtricitabine 300/200 mg coformulation once daily",
"        <strong>",
"         plus",
"        </strong>",
"        raltegravir 400 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Alternative regimens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenofovir-emtricitabine 300/200 mg coformulation once daily",
"        <strong>",
"         plus",
"        </strong>",
"        atazanavir 300 mg once daily&nbsp;with ritonanvir 100 mg once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenofovir-emtricitabine 300/200 mg coformulation once daily",
"        <strong>",
"         plus",
"        </strong>",
"        darunavir 800 mg once daily with ritonanvir 100 mg once daily taken&nbsp;with food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Additional possible regimens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenofovir-emtricitabine 300/200 mg coformulation once daily",
"        <strong>",
"         plus",
"        </strong>",
"        lopinavir-ritonavir 400/100 mg coformulation twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Efavirenz",
"        <sup>",
"         &bull;",
"        </sup>",
"        -tenofovir-emtricitabine 600/300/200 mg coformulation once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stavudine 30 mg twice daily&nbsp;",
"        <strong>",
"         plus",
"        </strong>",
"        either lamivudine 150 mg twice daily or emtricitabine 200 mg once daily,&nbsp;in place of tenfovir-emtricitabine in one of the above regimens, if tenofovir&nbsp;should be&nbsp;avoided (eg in setting of renal insufficiency)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    These are recommendations of the authors and are consistent with the 2012 United States Department of Health and Human Services guidelines for treatment of HIV-infected adults.",
"    <div class=\"footnotes\">",
"     * Usual duration of post-exposure prophylaxis (PEP) is 28 days.&nbsp;Doses shown are for adults with normal renal function.&nbsp;Dose adjustment for renal insufficiency may be needed.",
"     <br>",
"      &bull; Efavirenz is preferably avoided in women of child-bearing potential.&nbsp;Cognitive side effects due to efavirenz may be better tolerated by taking the coformulation at bedtime.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Panel on Antiretroviral Guidelines for Adults and Adolescents. Guidelines for the use of antiretroviral agents in HIV-1 infected adults and adolescents. Department of Health and Human Services. 1-239. Available at&nbsp;&nbsp;",
"     <a href=\"file://aidsinfo.nih.gov/contentfiles/lvguidelines/adultandadolescentgl.pdf\">",
"      file://aidsinfo.nih.gov/contentfiles/lvguidelines/adultandadolescentgl.pdf",
"     </a>",
"     &nbsp; Accessed&nbsp;27&nbsp;November 2012.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
