var title_f19_13_19677="Staging of thymic tumors";
var content_f19_13_19677=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F79311&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F79311&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical staging of thymic epithelial tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Masaoka's clinical stage",
"        <sup>",
"         [1]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage I: Macroscopically completely encapsulated and microscopically no capsular invasion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage II: Macroscopic invasion into surrounding fatty tissue or mediastinal pleura, or microscopic invasion into capsule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage III: Macroscopic invasion into neighboring organs, ie, pericardium, great vessels, or lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage IVa: Pleural or pericardial dissemination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage IVb: Lymphogenous or hematogenous metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Koga's proposed pathologic tumor extent of thymic epithelial tumors",
"        <sup>",
"         [2]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage I: Grossly and microscopically completely encapsulated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage II: Microscopic transcapsular invasion or macroscopic invasion into thymic or surrounding fatty tissue, or grossly adherent to but not breaking through mediastinal pleura or pericardium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage III: Macroscopic invasion of neighboring organ, (eg, pericardium, great vessels, or lung)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage IVa: Pleural or pericardial dissemination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        Stage IVb: Lymphogenous or hematogenous metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Yamakawa-Masaoka TNM classification and staging",
"        <sup>",
"         [3]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        T factor:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        T1: Macroscopically completely encapsulated and microscopically no capsular invasion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        T2: Macroscopically adhesion or invasion into surrounding fatty tissue or mediastinal pleura, or microscopic invasion into capsule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        T3: Invasion into neighboring organs, such as pericardium, great vessels, and lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        T4: Pleural or pericardial dissemination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        N factor:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        N0: No lymph node metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        N1: Metastasis to anterior mediastinal lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        N2: Metastasis to intrathoracic lymph nodes except anterior mediastinal lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        N3: Metastasis to extrathoracic lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        M factor:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        M0: No hematogenous metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"4\">",
"        M1: Hematogenous metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Stage:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage I",
"       </td>",
"       <td>",
"        T1",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage II",
"       </td>",
"       <td>",
"        T2",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage III",
"       </td>",
"       <td>",
"        T3",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage IVa",
"       </td>",
"       <td>",
"        T4",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Stage IVb",
"       </td>",
"       <td>",
"        any T",
"       </td>",
"       <td>",
"        N1, 2, 3",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        any T",
"       </td>",
"       <td>",
"        any N",
"       </td>",
"       <td>",
"        M1",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Masaoka A, Monden Y, Nakahara K, et al. Cancer 1981; 48:2485.",
"       </li>",
"       <li>",
"        Koga K, Matsuno Y, Noguchi M, et al. Pathol Int 1994; 44:359.",
"       </li>",
"       <li>",
"        Yamakawa Y, Masaoka A, Hashimot T, et al. Cancer 1991; 68:1984.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
