var title_f19_15_19708="Types of proarrhythmia of antiarrhythmic drugs";
var content_f19_15_19708=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F66133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F66133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Types of proarrhythmia during treatment with antiarrhythmic drugs (AADs) for atrial fibrillation or flutter according to the Vaughan Williams Classification",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ventricular proarrhythmia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Torsade de pointes (Vaughan Williams class IA and type III drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Sustained monomorphic ventricular tachycardia (usually class IC drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Sustained polymorphic ventricular tachycardia/ventricular fibrillation without long QT interval (class IA, IC, and III drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Atrial proarrhythmia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Provocation of recurrence (probably class IA, IC, and III drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Conversion of atrial fibrillation (AF) to flutter (usually class IC drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Increase of defibrillation threshold (a potential problem with class IC drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Abnormalities of conduction or impulse formation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Accelerate ventricular rate during AF (class IA and type IC drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Accelerate conduction over accessory pathway (digoxin, type IV drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; Sinus node dysfunction, atrioventricular block (almost all drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Vaughan Williams classification of AADs used for the treatment of atrial fibrillation or flutter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Class IA",
"        </strong>",
"        - Disopyramide, procainamide, quinidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Class IC",
"        </strong>",
"        - Flecainide, propafenone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Class III",
"        </strong>",
"        - Amiodarone, dofetilide, ibutilide, sotalol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Class IV",
"        </strong>",
"        - Nondihydropyridine calcium channel blockers (diltiazem and verapamil)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Fuster V, Ryden LE, Cannom DS, et al. ACC/AHA/ESC guidelines for the management of patients with atrial fibrillation. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines and the European Society of Cardiology Committee for Practice Guidelines (Writing committee to revise the 2001 guidelines for the management of patients with atrial fibrillation). J Am Coll Cardiol 2006; 48:e149. .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
