" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pharmacologic treatment of essential tremor",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/17/19737/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/17/19737/contributors\">",
"     Daniel Tarsy, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/17/19737/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/17/19737/contributors\">",
"     Howard I Hurtig, MD",
"    </a>",
"    <br/>",
"   </div>",
