var title_f19_17_19740="WHO classification lung cancer 2004";
var content_f19_17_19740=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F59692&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F59692&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    2004 WHO classification of invasive malignant epithelial lung tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Squamous cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variants: papillary, clear cell, small cell, basaloid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Small cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variant: combined small cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adenocarcinoma, mixed subtype",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acinar adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Papillary adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Bronchioloalveolar carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Variants: nonmucinous, mucinous, mixed nonmucinous and mucinous or indeterminate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Solid adenocarcinoma with mucin production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Variants: fetal adenocarcinoma, mucinous (\"colloid\") carcinoma, mucinous cystadenocarcinoma, signet ring adenocarcinoma, clear cell adenocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Large cell carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variants: large cell neuroendocrine carcinoma, combined large cell neuroendocrine carcinoma, basaloid carcinoma, lymphoepithelioma-like carcinoma, clear cell carcinoma, large cell carcinoma with rhaboid phenotype",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Adenosquamous carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Sarcomatoid carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variants: pleomorphic carcinoma, spindle cell carcinoma, giant cell carcinoma, carcinosarcoma, pulmonary blastoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Carcinoid tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variants: typical carcinoid, atypical carcinoid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Salivary gland tumors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variants: mucoepidermoid carcinoma, adenoid cystic carcinoma, epithelial-myoepithelial carcinoma",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Travis, WD, Brambilla, E, Muller-Hermlink, HK, Harris, CC (eds). World Health Organization classification of tumours. Pathology and genetics of tumours of the lung, pleura, thymus and heart. IARC Press. Lyon 2004.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
