var title_f19_2_19499="Principles of alternative remed";
var content_f19_2_19499=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F51906&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F51906&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Complementary and alternative therapies for rheumatic diseases: principles",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        If it sounds too good to be true, it is",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If you have not read about it in the scientific literature, it is not validated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If you first heard about it from the media, lay press, or your patients, it is not validated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There are no secrets in good science or good medicine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There have been no \"breakthrough\", important advances in rheumatology from \"complementary\" and \"alternative\" medicine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The question regarding trying \"complementary\" and \"alternative\" therapies should not be \"why not?\" but \"why?\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        This is reality. Deal with it. Discuss \"complementary\" and \"alternative\" therapies with patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I do not believe in \"complementary\" and \"alternative\" medicine. I believe in science, scientific methods, and a single high standard of good evidence-based medicine for all patients.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Bulletin on Rheumatic Diseases. Arthritis Foundation. Copyright &copy; 2002 The Arthritis Foundation",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
