var title_f19_21_19805="Risk death MI unstable angina";
var content_f19_21_19805=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F65662&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F65662&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Short-term risk of death or nonfatal MI in patients with UA/NSTEMI",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        High risk (at least one of the following features must be present):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        History:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Accelerating tempo of ischemic symptoms in preceding 48 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Character of pain:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prolonged ongoing (&gt;20 min) rest pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Clinical findings:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulmonary edema, most likely due to ischemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New or worsening MR murmur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        S",
"        <sub>",
"         3",
"        </sub>",
"        or new/worsening rales",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypotension, bradycardia, tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Age &gt;75 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        ECG:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Angina at rest with transient ST-segment changes &gt;0.5 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bundle-branch block, new or presumed new",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sustained ventricular tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Cardiac markers:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Elevated cardiac TnT, TnI, or CK-MB (eg, TnT or TnI &gt;0.1 ng per ml)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Intermediate risk (no high-risk feature but must have one of the following):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        History:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prior MI, peripheral or cerebrovascular disease, or CABG; prior aspirin use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Character of pain:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prolonged (&gt;20 min) rest angina, now resolved, with moderate or high likelihood of CAD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rest angina (&gt;20 min) or relieved with rest or sublingual NTG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nocturnal angina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New-onset or progressive CCS class III or IV angina in the past&nbsp;two weeks without prolonged (&gt;20 min) rest pain but with intermediate or high likelihood of CAD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Clinical findings:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Age &gt;70 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        ECG:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T-wave changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pathological Q waves or resting ST-depression less than 1 mm in multiple lead groups (anterior, inferior, lateral)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Cardiac markers:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Slightly elevated cardiac TnT, TnI, or CK-MB (eg, TnT greater than 0.01 but less than 0.1 ng per ml)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Low risk (no high- or intermediate-risk features, but may have any of the following features):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Character of pain:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Increased angina frequency, severity, or duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Angina provoked at a lower threshold",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New onset angina with onset&nbsp;two weeks to&nbsp;two months prior to presentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        ECG:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Normal or unchanged ECG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Cardiac markers:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Normal",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Anderson JL, Adams CD, Antman EM, et al. ACC/AHA 2007 guidelines for the management of patients with unstable angina/non-ST-Elevation myocardial infarction: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to Revise the 2002 Guidelines for the Management of Patients With Unstable Angina/Non-ST-Elevation Myocardial Infarction) developed in collaboration with the American College of Emergency Physicians, the Society for Cardiovascular Angiography and Interventions, and the Society of Thoracic Surgeons endorsed by the American Association of Cardiovascular and Pulmonary Rehabilitation and the Society for Academic Emergency Medicine. J Am Coll Cardiol 2007; 50:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
