" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F3149994\">",
"      Brand Names: Canada",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F193748\">",
