var title_f19_22_19821="Drugs and G6PD deficiency";
var content_f19_22_19821=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F74254&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F74254&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    PARTIAL list of drugs and chemicals thought to be safe or unsafe in individuals with glucose-6-phosphate dehydrogenase deficiency*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Unsafe for class I, II, and III variants",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Acetanilid",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Dapsone",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Furazolidone",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Methylene blue",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Nalidixic acid",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Naphthalene (mothballs, henna)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Niridazole",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Nitrofurantoin",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Phenazopyridine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Phenylhydrazine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Primaquine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfacetamide",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfamethoxazole",
"            <sup>",
"             &Delta;",
"            </sup>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfanilamide",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfapyridine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Thiazosulfone",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Toluidine blue",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Trinitrotoluene",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Uricase (rasburicase, pegloticase)",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Safe for class II and III variants",
"            <span class=\"small\">",
"             <sup>",
"              &bull;",
"             </sup>",
"            </span>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Acetaminophen",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Aminopyrine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Ascorbic acid (except in very high doses)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Aspirin",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Chloramphenicol",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Chloroquine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Colchicine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Diphenhydramine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Isoniazid",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            L-DOPA",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Menadione",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Paraaminobenzoic acid",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Phenacetin",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Phenytoin",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Probenecid",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Procainamide",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Pyrimethamine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Quinidine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Quinine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Streptomycin",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfamethoxpyridazine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Sulfisoxazole",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Trimethoprim",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Tripelennamine",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Vitamin K",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Note: This is a partial listing only. See text for information on how to obtain a more comprehensive listing of safe and unsafe agents.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Safety for class I variants is usually not known.",
"      <br>",
"       &Delta; The relative safety of this medication for patients with class II and III variants is unclear. See text for details.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Beutler E. G6PD deficiency.&nbsp;Blood 1994; 84:3613.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
