var title_f19_25_19867="Objectives of mechanical vent";
var content_f19_25_19867=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F78508&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F78508&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Objectives of mechanical ventilation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Physiologic objectives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Support pulmonary gas exchange based on alveolar ventilation and arterial oxygenation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce the metabolic cost of breathing by unloading the ventilatory muscles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minimize ventilator-induced lung injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical objectives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reverse hypoxemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reverse acute respiratory acidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Relieve respiratory distress",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prevent or reverse atelectasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reverse ventilatory muscle fatigue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Permit sedation and/or neuromuscular blockade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease systemic or myocardial oxygen consumption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stabilize the chest wall",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Slutsky, AS, Chest 1993; 104:1833.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
