var title_f19_26_19883="Questions that are helpful in detecting possible narcolepsy";
var content_f19_26_19883=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F83635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F83635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Questions that are helpful in detecting possible narcolepsy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Question",
"       </td>",
"       <td class=\"subtitle1\">",
"        Implication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are you sleepy most of the day?",
"       </td>",
"       <td>",
"        Sleepiness present, but could be caused by a variety of conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do you feel rested upon waking in the morning? Are your naps refreshing?",
"       </td>",
"       <td>",
"        Sleepiness probably not caused by poor quality sleep (eg, sleep apnea, etc)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do you have vivid dreams during daytime naps?",
"       </td>",
"       <td>",
"        Possible REM sleep during naps",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do you ever have muscle weakness when you tell a joke or laugh?",
"       </td>",
"       <td>",
"        Cataplexy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do you ever see, hear, or feel things that you know aren't there as you are falling asleep?",
"       </td>",
"       <td>",
"        Hypnagogic hallucinations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are you ever unable to move when you first awake or just before falling asleep?",
"       </td>",
"       <td>",
"        Sleep paralysis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
