var title_f19_26_19884="Prevention of dental caries in high-risk individuals";
var content_f19_26_19884=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F86225&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F86225&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prevention of dental caries in high-risk individuals",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Intervention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Rationale",
"       </td>",
"       <td class=\"subtitle1\">",
"        Directions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oral hygiene and non-cariogenic diet",
"       </td>",
"       <td>",
"        Decreases the substrate available for cariogenic bacteria",
"       </td>",
"       <td>",
"        <p>",
"         Brush teeth at least twice daily using soft-bristled toothbrush",
"        </p>",
"        <p>",
"         Daily use of dental floss",
"        </p>",
"        <p>",
"         Restrict the amount of refined carbohydrates in daily meals",
"        </p>",
"        Avoid sugary and acidic beverages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fluoride products",
"       </td>",
"       <td>",
"        Increases tooth surface resistance to acids and acts as a reservoir for remineralization of early caries",
"       </td>",
"       <td>",
"        <p>",
"         Fluoride toothpaste should be used twice/day",
"        </p>",
"        <p>",
"         Fluoride gel in tray should be applied daily",
"        </p>",
"        Fluoride varnish can be professionally applied up to four time per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium-phosphate based remineralizing agents",
"       </td>",
"       <td>",
"        <p>",
"         Casein phosphopeptide - amorphous calcium phosphate (CPP-ACP) product provide a source of calcium and phosphate",
"        </p>",
"        Facilitates remineralization of early carious lesions",
"       </td>",
"       <td>",
"        Use daily with fluoride",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Xylitol products",
"       </td>",
"       <td>",
"        Unlike sucrose or fructose, xylitol is not utilized by",
"        <em>",
"         S.mutans",
"        </em>",
"        as a source of energy",
"       </td>",
"       <td>",
"        <p>",
"         Use xylitol-containing products (gum, candy) three to five times per day",
"        </p>",
"        Fluoride toothpaste with at least 10 percent xylitol should be used twice/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Routine dental visits",
"       </td>",
"       <td>",
"        <p>",
"         Caries screening through clinical examination and bitewing radiographs",
"        </p>",
"        <p>",
"         Restoration of carious lesions",
"        </p>",
"        <p>",
"         Professional dental cleaning",
"        </p>",
"        Fluoride varnish application",
"       </td>",
"       <td>",
"        <p>",
"         Recall visits every six months",
"        </p>",
"        <p>",
"         Bitewings radiographs annually, or more frequent if high caries risk",
"        </p>",
"        Dental caries should be treated as soon as identified",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Nathaniel Treister, DMD, DMSc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
