var title_f19_27_19900="Features of idiopathic interstitial pneumonias";
var content_f19_27_19900=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F63326&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F63326&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Distinguishing features of the idiopathic interstitial pneumonias",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        AIP",
"       </td>",
"       <td class=\"subtitle1\">",
"        DIP",
"       </td>",
"       <td class=\"subtitle1\">",
"        UIP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prodrome",
"       </td>",
"       <td>",
"        Abrupt (1 to 2 weeks)",
"       </td>",
"       <td>",
"        Subacute (weeks to months)",
"       </td>",
"       <td>",
"        Chronic (&gt;12 months)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest radiography",
"       </td>",
"       <td>",
"        Diffuse, bilateral air-space opacification",
"       </td>",
"       <td>",
"        Ground-glass opacity in lower lung zones",
"       </td>",
"       <td>",
"        <p>",
"         Bilateral reticular opacities",
"        </p>",
"        <p>",
"         Honeycombing",
"        </p>",
"        <p>",
"         Lower zone predominance",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HRCT features",
"       </td>",
"       <td>",
"        <p>",
"         Bilateral, symmetric areas of ground-glass opacity",
"        </p>",
"        <p>",
"         Bilateral air space consolidation",
"        </p>",
"       </td>",
"       <td>",
"        Diffuse ground-glass opacity in middle and lower lungzones",
"       </td>",
"       <td>",
"        <p>",
"         Intralobular interstitial opacities",
"        </p>",
"        <p>",
"         Irregular interlobular septal thickening",
"        </p>",
"        <p>",
"         Traction bronchiectasis and honeycombing",
"        </p>",
"        <p>",
"         Lower zone predominance",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histopathologic features",
"       </td>",
"       <td>",
"        Diffuse, uniform organizing diffuse alveolar damage (hyaline membranes)",
"       </td>",
"       <td>",
"        Diffuse, uniform intraalveolar macrophage accumulation",
"       </td>",
"       <td>",
"        Variegated, patchy, subpleural interstitial fibrosisand honeycombing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment",
"       </td>",
"       <td>",
"        <p>",
"         Mechanical ventilation",
"        </p>",
"        <p>",
"         Glucocorticoid responsiveness unknown",
"        </p>",
"       </td>",
"       <td>",
"        Glucocorticoid responsive",
"       </td>",
"       <td>",
"        Poor response to glucocorticoids or cytotoxic agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prognosis",
"       </td>",
"       <td>",
"        60 percent mortality in &lt;6 months",
"       </td>",
"       <td>",
"        5 percent mortality in 5 years",
"       </td>",
"       <td>",
"        50 to 70 percent mortality in 5 years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AIP: acute interstitial pneumonia; UIP: usualinterstitial pneumonia; DIP: desquamative interstitial pneumonia; HRCT: high resolution CT scan.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
