var script_f19_33_19985=[" /* <![CDATA[ */",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function HarveyBradshaw_fx() {",
"with(document.HarveyBradshaw_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + 1;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc1[3].checked){",
"Score = Score + 3;",
"}",
"if (cc1[4].checked){",
"Score = Score + 4;",
"}",
"if (cc2[0].checked){",
"Score = Score + 0;",
"}",
"if (cc2[1].checked){",
"Score = Score + 1;",
"}",
"if (cc2[2].checked){",
"Score = Score + 2;",
"}",
"if (cc2[3].checked){",
"Score = Score + 3;",
"}",
"thisvalue = parseFloat(cc3.value);",
"if (isNaN(thisvalue)) thisvalue = 0;",
"ThisScore =  thisvalue * 1 + 0;",
"Score =  Score + ThisScore;",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 1;",
"}",
"if (cc4[2].checked){",
"Score = Score + 3;",
"}",
"if (cc4[3].checked){",
"Score = Score + 4;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"if (cc8.checked){",
"Score = Score + 1;",
"}",
"if (cc9.checked){",
"Score = Score + 1;",
"}",
"if (cc10.checked){",
"Score = Score + 1;",
"}",
"if (cc11.checked){",
"Score = Score + 1;",
"}",
"if (cc12.checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"}",
"}",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
