var title_f19_36_20043="Prevention outdoor infestation";
var content_f19_36_20043=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F51581&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F51581&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prevention of outdoor fire ant infestation at health care facilities",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        The first 25 to 50 feet around the outside of a building should be considered a critical zone and regularly surveyed for fire ant infestation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allow 18 to 20 inches of clear air space between the building and the mature height and width of ornamental plants.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If irrigation is used, make sure sprinkler heads are pointed away from the building and not positioned so as to wet the building, thus providing moisture for insect infestation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Keep the soil grade sloping away from the building to prevent accumulation of water against the foundation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If rain gutters are used, make sure that down spouts are clear and water runs freely away from the facility.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Contract with a pest control service to routinely inspect and, if appropriate, apply insecticides to keep the perimeter of health care facilities free of fire ant infestation.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Goddard J, Jarratt J, deShazo RD. Recommendations for prevention and management of fire ant infestation of health care facilities. South Med J 2002; 95:627. Copyright &copy; 2002 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
