var title_f19_37_20060="Acute NSAID toxicity - Rapid overview";
var content_f19_37_20060=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F59428&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F59428&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Acute NSAID toxicity: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        History",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ask specifically about acetaminophen and aspirin ingestion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ascertain type of NSAID, amount ingested, when ingested",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical and laboratory features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NSAIDS are usually well tolerated in overdose, with minimal toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe acute overdose may result in alterations in mental status, seizure, metabolic acidosis, or renal insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anaphylaxis may occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Basic electrolytes to assess concentrations and acid-base status; baseline renal function (indicated only in moderate to severe overdose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acetaminophen and salicylate concentrations to rule out concurrent pain medication ingestion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fingerstick glucose to rule out hypoglycemia as an etiology of any alteration in mental status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electrocardiogram to assess for toxin-induced prolongation of the QRS or QTc intervals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Arterial blood gas in severe overdose or altered mental status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pregnancy test for women of child-bearing age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Secure airway, breathing, and circulation (rarely an issue in pure NSAID poisoning)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Give gastrointestinal decontamination: activated charcoal, 1 g/kg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no antidote for NSAID poisoning; supportive care will usually suffice",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
