var title_f19_38_20075="Rabies preexposure prophylaxis";
var content_f19_38_20075=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F74185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F74185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rabies pre-exposure prophylaxis schedule - United States, 2008",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of vaccination",
"       </td>",
"       <td class=\"subtitle1\">",
"        Route",
"       </td>",
"       <td class=\"subtitle1\">",
"        Regimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primary",
"       </td>",
"       <td>",
"        Intramuscular",
"       </td>",
"       <td>",
"        Human diploid cell vaccine (HDCV) or purified chick embryo cell vaccine (PCECV); 1 mL (deltoid area), one each on days 0*, 7, and 21 or 28",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Booster&bull;",
"       </td>",
"       <td>",
"        Intramuscular",
"       </td>",
"       <td>",
"        HDCV or PCECV; 1 mL (deltoid area), day 0 only",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Day 0 is the day the first dose of vaccine is administered.",
"     <br>",
"      &bull; Persons in the continuous-risk category should have a serum sample tested for rabies virus neutralizing antibody every&nbsp;six months, and persons in the frequent-risk category should be tested every&nbsp;two years. An intramuscular booster dose of vaccine should be administered if the serum titer falls to maintain a value of at least complete neutralization at a 1:5 serum dilution by rapid fluorescent focus inhibition test.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Manning SE, Rupprecht CE, Fishbein D, et al. Human rabies prevention-United States, 2008: recommendations of the Advisory Committee on Immunization Practices. MMWR Recomm Rep 2008; 57:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
