" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Chronic menorrhagia or anovulatory uterine bleeding",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/41/20122/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/41/20122/contributors\">",
"     Howard A Zacur, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/41/20122/contributors\">",
"     Section Editor",
"    </a>",
