" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Etiology, prenatal diagnosis, obstetrical management, and recurrence of orofacial clefts",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Louise Wilkins-Haug, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Deborah Levine, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Helen V Firth, DM, FRCP, DCH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/44/20169/contributors\">",
"     Vanessa A Barss, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
