var title_f19_46_20204="Classes of ERCP complications";
var content_f19_46_20204=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F73512&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F73512&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of site, timing, and severity of complications related to endoscopic retrograde cholangiopancreatography (ERCP)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focal",
"       </td>",
"       <td>",
"        Occurring at the point of endoscopic contact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonspecific",
"       </td>",
"       <td>",
"        Occurring in organs not traversed or treated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immediate",
"       </td>",
"       <td>",
"        Occurring during ERCP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Early",
"       </td>",
"       <td>",
"        Evident within the recovery period",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Delayed",
"       </td>",
"       <td>",
"        Focal (occurring within 30 days)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonspecific (first symptom presenting within 3 days)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Late",
"       </td>",
"       <td>",
"        Evident after months or years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Criteria for severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Length of stay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mild",
"       </td>",
"       <td>",
"        &le;3 nights",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moderate",
"       </td>",
"       <td>",
"        4 to 10 nights",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe",
"       </td>",
"       <td>",
"        &gt;10 nights, ICU admission, or surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fatal",
"       </td>",
"       <td>",
"        Death attributable to the procedure within 30 days (or longer if under continued in-patient treatment for the complication)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Other indices of severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Need for blood transfusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Additional interventions (endoscopic or radiologic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Total length of stay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Permanent residual disability",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Cotton PB, Gastrointest Endosc 1994; 40:514 and Aliperti G, et al, Gastrointest Endosc Clin N Am 1996; 6:379.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
