var title_f19_48_20236="Evaluation chronic HBV";
var content_f19_48_20236=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F62875&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F62875&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of patients with chronic HBV infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Initial evaluation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. History and physical examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Family History of liver disease, HCC",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Laboratory tests to assess liver disease - complete blood counts with platelets, hepatic panel, and prothrombin time",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Tests for HBV replication - HBeAg/anti-HBe, HBV DNA",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Tests to rule out viral coinfections - anti-HCV, anti-HDV (in persons from countries where HDV infection is common and in those with history of injection drug use), and anti-HIV in those at risk",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Tests to screen for HCC - AFP at baseline and, in high risk patients, ultrasound",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Consider liver biopsy to grade and stage liver disease - for patients who meet criteria for chronic hepatitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Suggested follow-up for patients not considered for treatment HBeAg+, HBV DNA &gt;20,000 int. units/mL and normal ALT",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ALT every 3-6 months, more often if ALT becomes elevated",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If ALT levels are between 1-2 x ULN, recheck ALT every 1-3 months; consider liver biopsy if age &gt;40, ALT borderline or mildly elevated on serial tests. Consider treatment if biopsy shows moderate/severe inflammation or significant fibrosis.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If ALT &gt;2 x ULN for 3-6 months and HBeAg+, HBV DNA &gt;20,000 int. units/mL, consider liver biopsy and treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider screening for HCC in relevant population",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Inactive HBsAg carrier state",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ALT every 3 months for 1 year, if persistently normal, ALT every 6-12 months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If ALT &gt;1-2 x ULN, check serum HBV DNA level and exclude other causes of liver disease. Consider liver biopsy if ALT borderline or mildly elevated on serial tests or if HBV DNA persistently &ge;2000 int. units/mL. Consider treatment if biopsy shows moderate/severe inflammation or significant fibrosis.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider screening for HCC in relevant population",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Lok ASF, McMahon BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8th 2009. Copyright &copy; 2009 American Association for the Study of Liver Diseases.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
