var title_f19_52_20300="RBC transfusion guidelines";
var content_f19_52_20300=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F78925&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F78925&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for red cell transfusions and volume replacement in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"1\" rowspan=\"1\">",
"        Need based on estimation of lost blood volume:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        &gt;40 percent loss (&gt;2000 mL):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rapid volume replacement, including RBC transfusion, is required",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        30 to 40 percent loss (1500 to 2000 mL):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rapid",
"volume replacement with crystalloids or synthetic colloids is",
"required; RBC transfusion will probably also be required",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        15 to 30 percent loss (800 to 1500 mL):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Need to transfuse crystalloids or synthetic colloids; need for RBC",
"transfusion is unlikely unless the patient has pre-existing",
"anemia, continuing blood loss, or reduced cardiovascular reserve",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Less than 15 percent blood loss (&le;750 mL):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No need for transfusion unless volume loss is superimposed on",
"preexisting anemia, or when patient is unable to compensate due to",
"severe cardiac or respiratory disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"1\" rowspan=\"1\">",
"        Need based on hemoglobin concentration:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Hgb &lt;7 g/dL:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        RBC",
"transfusion indicated. If the patient is otherwise stable, the patient",
"should receive 2 units of packed RBC, following which the patient's",
"clinical status and circulating HgB should be reassessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Hgb 7 to 10 g/dL:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Correct strategy is unclear",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Hgb &gt;10 g/dL:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        RBC transfusion not indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        High risk patients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients &gt;65 and/or those with cardiovascular or respiratory disease",
"may tolerate anemia poorly. Such patients may be transfused when Hgb",
"&lt;8 g/dL.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Murphy, MF, et al. British Committee for Standards in Haematology, Blood Transfusion Task Force. Br J Haematol 2001; 113:24.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
