var title_f19_52_20301="Acceptable regimens for ART-naive patients";
var content_f19_52_20301=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F82655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F82655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Acceptable antiretroviral regimens for treatment-na&iuml;ve patients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Acceptable regimens (CI)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        (Regimens that may be selected for some patients but are less satisfactory than preferred or alternative regimens) and regimens that may be acceptable but more definitive data are needed (CIII).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         NNRTI-based regimen",
"        </strong>",
"        <ul>",
"         <li>",
"          EFV + ddI + (3TC or FTC) (CI)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         PI-based regimens",
"        </strong>",
"        <ul>",
"         <li>",
"          ATV + (ABC or ZDV)/3TC* (CI)",
"         </li>",
"         <li>",
"          DRV/r + (ABC or ZDV)/3TC* (CIII)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         INSTI-based regimen",
"        </strong>",
"        <ul>",
"         <li>",
"          RAL + (ABC or ZDV)/3TC* (CIII)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         CCR5 antagonist-based regimens",
"        </strong>",
"        <ul>",
"         <li>",
"          MVC + ZDV/3TC* (CI)",
"         </li>",
"         <li>",
"          MVC + TDF/FTC* or ABC/3TC* (CIII)",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <strong>",
"         Comments",
"        </strong>",
"        <hr/>",
"        <p>",
"         <strong>",
"          EFV + ddI + (FTC or 3TC)",
"         </strong>",
"         has only been studied in small clinical trials.",
"        </p>",
"        <br/>",
"        <p>",
"         <strong>",
"          ATV/r",
"         </strong>",
"         is generally preferred over ATV. Unboosted ATV may be used when RTV boosting is not possible.",
"        </p>",
"        <br/>",
"        <p>",
"         <strong>",
"          MVC.",
"         </strong>",
"         Tropism testing should be performed before initiation of therapy; only patients found to have only CCR5-tropic virus are candidates for MVC.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Regimens that may be acceptable but should be used with caution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        (Regimens that have demonstrated virologic efficacy in some studies but have safety, resistance, or efficacy concerns. See comments below.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         NNRTI-based regimens",
"        </strong>",
"        <ul>",
"         <li>",
"          NVP + ABC/3TC* (CIII)",
"         </li>",
"         <li>",
"          NVP + TDF/FTC* (CIII)",
"         </li>",
"        </ul>",
"        <hr/>",
"        <strong>",
"         PI-based regimens",
"        </strong>",
"        <ul>",
"         <li>",
"          FPV + [(ABC or ZDV)/3TC* or TDF/FTC*] (CIII)",
"         </li>",
"         <li>",
"          SQV/r + TDF/FTC* (CI)",
"         </li>",
"         <li>",
"          SQV/r + (ABC or ZDV)/3TC* (CIII)",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <strong>",
"         Comments",
"        </strong>",
"        <hr/>",
"        <p>",
"         Use",
"         <strong>",
"          NVP",
"         </strong>",
"         and",
"         <strong>",
"          ABC",
"         </strong>",
"         together with caution because both can cause HSRs within first few weeks after initiation of therapy.",
"        </p>",
"        <br/>",
"        <p>",
"         Early virologic failure with high rates of resistance has been reported in some patients receiving",
"         <strong>",
"          NVP + TDF + (3TC or FTC)",
"         </strong>",
"         . Larger clinical trials are currently in progress.",
"        </p>",
"        <br/>",
"        <p>",
"         <strong>",
"          FPV/r",
"         </strong>",
"         is generally preferred over unboosted FPV. Virologic failure with unboosted FPV-based regimen may select mutations that confer cross resistance to DRV.",
"        </p>",
"        <br/>",
"        <strong>",
"         SQV/r",
"        </strong>",
"        <ul>",
"         <li>",
"          SQV/r was associated with PR and QT prolongation in a healthy volunteer study",
"         </li>",
"         <li>",
"          Baseline ECG is recommended before initiation of SQV/r",
"         </li>",
"         <li>",
"          SQV/r is not recommended in patients with any of the following:",
"          <ol>",
"           <li>",
"            Pretreatment QT interval &gt;450 msec",
"           </li>",
"           <li>",
"            Refractory hypokalemia or hypomagnesemia",
"           </li>",
"           <li>",
"            Concomitant therapy with other drugs that prolong QT interval",
"           </li>",
"           <li>",
"            Complete AV block without implanted pacemaker",
"           </li>",
"           <li>",
"            Risk of complete AV block",
"           </li>",
"          </ol>",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     3TC: lamivudine; ABC: abacavir; ATV: atazanavir; ATV/r: atazanavir/ritonavir; AV: atrioventricular; ddI: didanosine; DRV: darunavir; DRV/r: darunavir/ritonavir; ECG: electrocardiogram; EFV: efavirenz; FPV: fosamprenavir; FPV/r: fosamprenavir/ritonavir; FTC: emtricitabine; HSR: hypersensitivity reaction; INSTI: integrase strand transfer inhibitor; msec: millisecond; MVC: maraviroc; NNRTI: non-nucleoside reverse transcriptase inhibitor; NVP: nevirapine; PI: protease inhibitor; RAL: raltegravir; RTV: ritonavir; SQV: saquinavir; SQV/r: saquinavir/ritonavir; TDF: tenofovir; ZDV: zidovudine.",
"     <br>",
"      * 3TC may be substituted with FTC or vice versa.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Guidelines for the use of antiretroviral agents in HIV-1-infected adults and adolescents, January 10, 2011. The United States Department of Health and Human Services.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
