var title_f19_53_20315="Sx of gonadotroph adenomas";
var content_f19_53_20315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F58084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F58084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical presentations of gonadotroph adenomas",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neurologic symptoms (most common)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visual impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other (including diplopia, seizures, and CSF rhinorrhea)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Incidental finding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        When an imaging procedure is performed because of an unrelated symptom",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hypopituitarism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Biochemical evidence (most common)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clinical symptoms (less common, but include oligomenorrhea or amenorrhea in women, decreased libido and/or erectile dysfunction in men)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical syndromes due to hormonal hypersecretion (rare)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ovarian hyperstimulation when FSH is secreted excessively in a premenopausal woman",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Premature puberty when intact LH is secreted in a prepubertal boy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
