var title_f19_53_20316="Skin lesions SLE";
var content_f19_53_20316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F75563&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F75563&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Skin lesions associated with lupus erythematosus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        I. Specific skin disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        A. Localized Acute Cutaneous LE (ACLE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Localized ACLE (malar rash, butterfly rash)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Inflammatory periorbital edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Mucous membrane lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. Oral and nasopharyngeal ulceration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5. Alopecia (fractured frontal hair, scarring and nonscarring alopecia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        B. Generalized ACLE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Disseminated maculopapular eruption &plusmn; butterfly rash",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Photosensitive lupus erythematosus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Toxic epidermal necrolysis-like eruption (apoptotic panepidermal necrolysis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. Bullous SLE (epidermolysis bullosa acquisita)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        C. Subacute Cutaneous LE (SCLE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Annular SCLE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Papulosquamous SCLE (psoriasiform, pityriasiform, and photosensitive LE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        D. Chronic Cutaneous LE (CCLE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Classical discoid LE (localized or generalized)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Hypertrophic/verrucous discoid LE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Lupus profundus/lupus panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. Mucosal discoid LE (oral or conjunctival)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5. Lupus tumidius (tumid LE, urticarial plaque of LE)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        6. Chilblains LE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        7. Lichenoid discoid LE (LE/lichen planus overlap)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        8. Palmar-plantar DLE",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Samuel L Moschella, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
