" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Principles of burn reconstruction: Overview of surgical procedures",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/55/20346/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/55/20346/contributors\">",
"     Jorge Leon-Villapalos, MD, FRCS",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?19/55/20346/contributors\">",
"     Peter Dziewulski, MD, FRCS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/55/20346/contributors\">",
"     Section Editor",
