" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Sports-related groin pain or 'sports hernia'",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/57/20373/contributors\">",
"     Author",
"    </a>",
"    <br/>",
