" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Inpatient management of traumatic rib fractures",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?19/6/19562/contributors\">",
